// page init
jQuery(function(){
	initTouchNav();
	initSlideShow();
	initOpenClose();
	initBackgroundResize();
	initSameHeight();
});

// fade galleries init
function initSlideShow() {
	jQuery('.testimonials .mask').fadeGallery({
		slides: 'blockquote',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('.tweets').fadeGallery({
		slides: 'blockquote',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('.product-slides').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.thumbs-paging li',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
}
