// stretch background to fill blocks
function initBackgroundResize() {
	jQuery('.bg-stretch').each(function() {
		ImageStretcher.add({
			container: this,
			image: 'img'
		});
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.posts').sameHeight({
		elements: '.box',
		flexible: true,
		multiLine: true
	});
	jQuery('.container').sameHeight({
		elements: '.banner, .team',
		flexible: true,
		multiLine: true
	});
	jQuery('.container').sameHeight({
		elements: '.banner, .team',
		flexible: true,
		multiLine: true
	});
	jQuery('#main2').sameHeight({
		elements: '#content, #aside',
		flexible: true,
		multiLine: true
	});
	jQuery('.prodcut-intro').sameHeight({
		elements: '.prodcut-info, .product-slides',
		flexible: true,
		multiLine: true
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('.thumbs li, .blogs .video').each(function(){
		new TouchNav({
			navBlock: this,
			menuItems: 'a'
		});
	});
	jQuery('.buttons').each(function(){
		new TouchNav({
			navBlock: this,
			menuItems: 'li'
		});
	});
	jQuery('.links ul').each(function(){
		new TouchNav({
			navBlock: this,
			menuItems: 'li'
		});
	});
}

